INSERT INTO `tp_archives` VALUES ('27', '人力资源服务', '人力，新乡人力，人力资源', '人力资源服务平台', '新誉佳集团', '新誉佳集团', '20180820\\61b244ef315405ebe54861aa45c5992c.jpg', '头条,推荐', '200', '<p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; border: 0px none; outline: 0px; color: rgb(116, 105, 105); line-height: 22px; text-align: justify; font-size: 12px; white-space: normal; font-family: &quot;Microsoft Yahei&quot; !important;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px; color: rgb(0, 0, 0);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;河南省新誉佳人力资源有限公司，成立于2007年，是经河南省人力资源和社会保障厅、工商行政管理部门批准注册成立，具有独立法人经营资格的专业人力资源服务机构，也是目前新乡地区专业度最高、资源最丰富的人力资源服务机构之一，也是把招聘、培训、安置、服务、维权融为一体的倡导者。公司致力于打造中国专业化程度最高，嵌入程度最深的人力服务方案提供机构。</span></p><p><br/></p>', '28', '28', '1534561304');
